const NuevoProyecto = () => {

    //state para proyecto
    const [proyecto, guardarProyecto] = useState({
        nombre:''
    });

    const onChangeProyecto = e => {
        guardarProyecto({
            ...proyecto,
            [e.target.name]: e.target.value
        })
    }


    return (

        <Fragment>

            <button
                type="button"
                className="btn btn-block btn-primario"
            >
                Nuevo proyecto
            </button>

            <form
                className="formulario-nuevo-proyecto"
            >
                <input
                    type="text"
                    className="input-text"
