import React, { Fragment } from 'react';

const Tarea = ({tarea}) => {
    return ( 
        <Fragment>
            <div className="estado">
            <li className="tarea sombra">
                <p>{tarea.nombre}</p>
            </li>


                {tarea.estado 
                    ? 
                        <button 
                            className="completo" type="button">
                                Completo
                        </button> 
                    :    
                        <button 
                            className="incompleto" type="button">
                                Incompleto
                        </button>
                }
            </div>
        </Fragment>
     );
}

export default Tarea;
